import math

def archimeds(numSides):
    innerAngeleB = 360.0/numSides
    halfangelA = innerAngeleB / 2
    oneHalfSideS = math.sin(math.radians(halfangelA))
    sideS = oneHalfSideS * 2
    polygonCircumference = numSides * sideS
    pi = polygonCircumference / 2
    return pi
print(archimeds(10000000001))
